const open_modal1 = document.getElementById("open_modal1");
const close_modal1 = document.getElementById("close_modal1");
const modal1 = document.getElementById("modal1")


window.addEventListener('click', function (e) {

    if (open_modal1.contains(e.target)) {
        modal1.style.display = "flex";

    } else if (close_modal1.contains(e.target)) {
        modal1.style.display = "none";

    }

})

